import React from 'react';
import ReactDOM from 'react-dom';
import { mount } from 'enzyme';
import LoadingBox from '.';
describe('LoadingBox', function () {
  it('renders without crashing', function () {
    var div = document.createElement('div');
    ReactDOM.render(React.createElement(LoadingBox, null, "example"), div);
  });
  it('should render a SVG when loading', function () {
    expect(mount(React.createElement(LoadingBox, {
      loading: true
    }, "example")).find('svg')).toHaveLength(1);
  });
  it('should render a SVG title when passed the value', function () {
    expect(mount(React.createElement(LoadingBox, {
      loading: true,
      title: "cat"
    }, "example")).find('title').text()).toBe('cat');
  });
  it('shouldnt render a SVG when not loading', function () {
    expect(mount(React.createElement(LoadingBox, {
      loading: false
    }, "example")).find('svg')).toHaveLength(0);
  });
  it('matches wrapper loading', function () {
    expect(mount(React.createElement(LoadingBox, {
      loading: true
    }, "example"))).toMatchSnapshot('wrapper loading');
  });
});
